INSERT INTO `emails` (`id`, `name`, `appOrTicket`, `builtIn`, `subject`, `body`, `defaultSubject`, `defaultBody`) VALUES
(1, 'confirmBooking', 1, 1, '{{$global->eventName}} Ticket Booking Confirmation', 'To {{$primary->name}},\n\nThank you for buying {{$global->eventName}} tickets. This email confirms that you have reserved your tickets to the ball.\n\nPlease see the instructions below to make payment. If you have already paid, do not worry you will likely receive a payment confirmation shortly. If you do not, please contact {{$global->ticketingEmail}}.\nPayment must be made by Midday on Friday the 7th of June 2013.\nPlease note that this email has only been sent to you, the primary ticket holder, and not to any additional guests you have reserved tickets for.\nTicket collection will take place in Easter term from Downing College. The full details for this will be announced nearer the time.\n\nApplication ID: {{$application->appID}}\n\nYour Details\n============\n{{$primary->name}}\n{{$primary->college}}\n{{$primary->address}}\nPhone: {{$primary->phone}}\n\nOrder Summary\n================\n{{foreach $application->tickets as $guest}} {{$guest->name}} ({{$guest->crsid}}) - {{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} - Hash Code: {{$guest->hashCode}}\n{{endeach}}\n\nTotal: {{$global->pound}}{{$application->totalCost}}\n\nPayment Information\n=================\nPlease make a bank transfer to {{$global->bankAccountName}} (if you cannot put the full name in, include as much as possible)\nSort Code: {{$global->bankSortCode}}\nAccount Number: {{$global->bankAccountNumber}}\nPayment Reference: {{$application->paymentReference}}\n\nPlease make absolutely sure that you quote the Payment Reference above. It includes your full name (or as much as can fit in 18 characters) and Application ID, which is ESSENTIAL information.\n\nPlease also email {{$global->paymentEmail}} with your Application ID and amount you paid in the subject, to indicate that you have made a payment. In the email body, please include the names of the guests covered by the payment.\n\nAn email confirming payment will be sent to the primary ticket holder when the entire balance has been successfully transferred. If you have not received this email within a few days of the full payment being made, please email {{$global->paymentEmail}} \n\n\nOther Information\n=================\nIf you need to change the name on any ticket, because you or one of your guests can no longer attend, then please fill in the details of the change at this website: http://www.downingball.co.uk/tickets/namechange.php\nThe fee for performing a name change is currently Â£20, but will increase nearer the ball.\n\nIf you notice any mistakes with your application then please reply to this email with details of the mistake.\n\nThank you for reserving {{$global->eventName}} tickets,\nWe look forward to seeing you on {{$global->eventFullDate}},\n\n{{$global->eventName}} Committee\n', '{{$global->eventName}} Ticket Booking Confirmation', 'To {{$primary->name}},\r\nThank you for buying {{$global->eventName}} tickets. This email confirms that you have reserved your tickets to the ball.\r\nPlease see the instructions below to make payment. If you have already paid, don''t worry you''ll likely receive a payment confirmation shortly. If you don''t, please contact {{$global->ticketingEmail}}.\r\nPayment must be made within 7 days.\r\n\r\nApplication ID: {{$application->appID}}\r\n\r\nYour Details\r\n============\r\n{{$primary->name}}\r\n{{$primary->college}}\r\n{{$primary->address}}\r\nPhone: {{$primary->phone}}\r\n\r\nOrder Summary\r\n================\r\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} {{$guest->hashCode}} {{$guest->crsid}}\r\n{{endeach}}\r\n\r\nTotal: {{$global->pound}}{{$application->totalCost}}\r\n\r\nPayment Information\r\n=================\r\nPlease make a bank transfer to ''{{$global->bankAccountName}}''.\r\nSort Code: {{$global->bankSortCode}}\r\nAccount Number: {{$global->bankAccountNumber}}\r\nPayment Reference: {{$application->paymentReference}}\r\n\r\nPlease make absolutely sure that you quote the Payment Reference above. It includes your full name (or as much as can fit in 18 characters) and Application ID, which is ESSENTIAL information.\r\n\r\nPlease also email {{$global->paymentEmail}} with your Application ID and amount you paid in the subject, to indicate that you have made a payment.\r\n\r\nIf you need to change the name on any ticket, because you or one of your guests can no longer attend, then please fill in the details of the change at this website:\r\n{{$global->eventWebsiteTickets}}namechange.php \r\nIf you notice any minor mistakes with your application, then please reply to this email with details of the mistake.\r\n\r\nThank you for reserving {{$global->eventName}} tickets,\r\nWe look forward to seeing you on {{$global->eventFullDate}},\r\n\r\n{{$global->eventName}} Committee'),
(2, 'afterBall', 0, 1, 'Thank you for attenting the {{$global->eventName}}', 'To {{$ticket->name}},\n\nWe hope that you enjoyed the ball on Monday night.\n\nFirst, we''d like to let you know about lost property...\nPhotos from the ball are available from...\n\nThanks once again for attending,\n\n{{$global->eventName}} Committee', 'Thank you for attenting the {{$global->eventName}}', 'To {{$ticket->name}},\r\n\r\nWe hope that you enjoyed the ball on Monday night.\r\n\r\nFirst, we''d like to let you know about lost property...\r\nPhotos from the ball are available from...\r\n\r\nThanks once again for attending,\r\n\r\n{{$global->eventName}} Committee'),
(3, 'entranceDetailsCollegeStaffAndFellows', 1, 1, '{{$global->eventName}} - Entrance Details', '-----------------------------------------------------------------------------\nYou are receiving this email as you are the main applicant on your {{$global->eventName}} order.\n\nPlease ensure your guests receive this information!\n-----------------------------------------------------------------------------\nDear {{$primary->name}},\n\nThe ball is only 2 days away! This email contains the details of entry to the ball on the evening of Monday 18th June. Guests may arrive separately, so please forward this email to them so that they are aware of timings.\n\nAs you have a fellow/staff ticket (or other special ticket), entry will be via the main Chimney entrance on Jesus Lane (for you and all your guests).\nIf any of your guests do not hold a similar ticket which allow Chimney entry, then you will have to enter the ball with them to make sure they are allowed Chimney access.\n\nFor a Standard ticket (i.e. not Priority or Dining), the ball starts at 8pm, so please arrive around that time.\nFor a Priority ticket, please arrive by 7:15pm, when the champagne reception will begin.\nFor a Dining ticket, the drinks reception will begin at 6:30pm, with the meal following at 7pm.\n        \nPlease note that all tickets allow entry up until 10pm, so you must arrive before this time otherwise you will not be allowed into the ball. Please also note that ALL guests must show their ticket along with some form of identification (preferably University ID, but otherwise drivers license or passport).\n        \nHere is one final chance to check over your tickets and ensure that everything is in order. If there are any issues, particularly those which you have previously emailed about and have not been corrected, please email me back asap.\n\nApplication Details:\n\nApplication ID: {{$application->appID}}\n	\nYour Details\n============\n{{$primary->name}}\n{{$primary->college}}\n{{$primary->address}}\nPhone: {{$primary->phone}}\n\nOrder Summary\n================\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} - {{$guest->name}} ({{$guest->hashCode}})\n{{endeach}}\n\nTotal: {{$global->pound}}{{$application->totalCost}}\n\nWe hope you are as excited about the ball as we are, and we look forward to seeing you there!\n\nKind regards, \n\nthe {{$global->eventName}} Committee', '{{$global->eventName}} - Entrance Details', '-----------------------------------------------------------------------------\r\nYou are receiving this email as you are the main applicant on your {{$global->eventName}} order.\r\n\r\nPlease ensure your guests receive this information!\r\n-----------------------------------------------------------------------------\r\nDear {{$primary->name}},\r\n\r\nThe ball is only 2 days away! This email contains the details of entry to the ball on the evening of Monday 18th June. Guests may arrive separately, so please forward this email to them so that they are aware of timings.\r\n\r\nAs you have a fellow/staff ticket (or other special ticket), entry will be via the main Chimney entrance on Jesus Lane (for you and all your guests).\r\nIf any of your guests do not hold a similar ticket which allow Chimney entry, then you will have to enter the ball with them to make sure they are allowed Chimney access.\r\n\r\nFor a Standard ticket (i.e. not Priority or Dining), the ball starts at 8pm, so please arrive around that time.\r\nFor a Priority ticket, please arrive by 7:15pm, when the champagne reception will begin.\r\nFor a Dining ticket, the drinks reception will begin at 6:30pm, with the meal following at 7pm.\r\n        \r\nPlease note that all tickets allow entry up until 10pm, so you must arrive before this time otherwise you will not be allowed into the ball. Please also note that ALL guests must show their ticket along with some form of identification (preferably University ID, but otherwise drivers license or passport).\r\n        \r\nHere is one final chance to check over your tickets and ensure that everything is in order. If there are any issues, particularly those which you have previously emailed about and have not been corrected, please email me back asap.\r\n\r\nApplication Details:\r\n\r\nApplication ID: {{$application->appID}}\r\n	\r\nYour Details\r\n============\r\n{{$primary->name}}\r\n{{$primary->college}}\r\n{{$primary->address}}\r\nPhone: {{$primary->phone}}\r\n\r\nOrder Summary\r\n================\r\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} - {{$guest->name}} ({{$guest->hashCode}})\r\n{{endeach}}\r\n\r\nTotal: {{$global->pound}}{{$application->totalCost}}\r\n\r\nWe hope you are as excited about the ball as we are, and we look forward to seeing you there!\r\n\r\nKind regards, \r\n\r\nthe {{$global->eventName}} Committee'),
(4, 'entranceDetailsGuests', 1, 1, 'Important: {{$global->eventName}} - Entrance Details', 'Dear {{$primary->name}},\n\nThis email contains the details of entry to the Ball on the evening of Tuesday the 18th of June. Your guests may arrive separately, so please forward this email to them so that they are aware of timings.\n\nPlease note that ALL guests must show their PHYSICAL TICKET along with PHOTOGRAPHIC IDENTIFICATION (university ID, drivers license or passport). The name on the ticket presented must match the name on the identification shown â€“ if they donâ€™t, entry will be denied.\n\nEntry for all guests will be through the gates next to the Portersâ€™ Lodge, on Regent Street. At these gates, all guests will be asked to present their tickets for an initial check.\nHolders of Standard tickets will enter through the gate closest to the Portersâ€™ Lodge and should queue along Regent Street and onto Lensfield Road. The Ball will start at 8:30pm, but wristband distribution will begin before this.\nQueue Jump, VIP and Dining guests should head directly to the other pedestrian gate near the Porter''s Lodge - next to the charity shop. Dining guests are requested to arrive at 6:15pm, and VIP guests at 7:15pm, as wristbands must be issued before the eveningâ€™s entertainment can begin. Queue Jump guests will be prioritised for entry to the main ball from 8:30.\n\nIf you, or your guest, have any mobility requirements please let us know immediately.\n\nLast entry to the Ball will be 11pm.\n\nPlease check the details below and ensure that everything is in order. We are in the process of completing a few last minute name changes, but if there are any issues please reply to this email immediately.\n\nYour Details\n============\n{{$primary->name}}\nApplication ID: {{$application->appID}}\n\nOrder Summary\n================\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$guest->name}} \n{{endeach}}\n\nSee you all soon!!\nThe {{$global->eventName}} Committee\n\nPS- Donâ€™t forget to download our May Ball App on iTunes!', '{{$global->eventName}} - Entrance Details', '-----------------------------------------------------------------------------\r\nYou are receiving this email as you are the main applicant on your {{$global->eventName}} order.\r\n\r\nPlease ensure your guests receive this information!\r\n-----------------------------------------------------------------------------\r\nDear {{$primary->name}},\r\n\r\nThe ball is only 2 days away! This email contains the details of entry to the ball on the evening of Monday 18th June. Guests may arrive separately, so please forward this email to them so that they are aware of timings.\r\n\r\nFor a Standard ticket, entry will be via the wooden Library Court gate on Jesus Lane (not the vehicle exit gate). The ball starts at 8pm, but wristbanding will start earlier to speed up the entry process.\r\nFor a Priority ticket, entry will be via the main Chimney entrance on Jesus Lane. Please arrive by 7:15pm, when the champagne reception will begin.\r\nFor a Dining ticket, entry will also be via the main Chimney entrance on Jesus Lane. The drinks reception will begin at 6:30pm, with the dinner following at 7pm.\r\n\r\nPlease note that all tickets allow entry up until 10pm, so you must arrive before this time otherwise you will not be allowed into the ball. Please also note that ALL guests must show their ticket along with some form of identification (preferably University ID, but otherwise drivers license or passport).\r\n        \r\nHere is one final chance to check over your tickets and ensure that everything is in order. If there are any issues, particularly those which you have previously emailed about and have not been corrected, please email me back asap.\r\n\r\nApplication Details:\r\n\r\nApplication ID: {{$application->appID}}\r\n	\r\nYour Details\r\n============\r\n{{$primary->name}}\r\n{{$primary->college}}\r\n{{$primary->address}}\r\nPhone: {{$primary->phone}}\r\n\r\nOrder Summary\r\n================\r\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} - {{$guest->name}} ({{$guest->hashCode}})\r\n{{endeach}}\r\n\r\nTotal: {{$global->pound}}{{$application->totalCost}}\r\n\r\nWe hope you are as excited about the ball as we are, and we look forward to seeing you there!\r\n\r\nKind regards, \r\n\r\nthe {{$global->eventName}} Committee'),
(5, 'paymentFirstWarning', 1, 1, '{{$global->eventName}} - Reminder of Payment Deadline Warning', 'To {{$primary->name}},\n\nThank you again for buying {{$global->eventName}} tickets.\nThis email is to let you know that we have not yet received full payment for your ticket(s), and the deadline has now passed.\n\nApplication ID: {{$application->appID}}\n\nTotal outstanding payment: {{$global->pound}}{{$application->totalToPay}}\n    \nPlease ensure the Treasurer receives your payment no later than Midnight on Sunday the 10th February 2013. After this time we will begin allocating tickets to the waiting list and therefore cannot guarantee your ticket(s).\nIf you have made payment in parts, there is no need to resend payments. The figure above may be incorrect as we will only mark your tickets valid once we have received the full payment. Please continue sending payment only up to the full amount and we will send confirmation once complete.\n\nPayment Information\n=================\nPlease make a bank transfer to: {{$global->bankAccountName}} (if you cannot put the full name in, include as much as possible)\nSort Code: {{$global->bankSortCode}}\nAccount Number: {{$global->bankAccountNumber}}\nPayment Reference: {{$application->paymentReference}}\n\nPlease make absolutely sure that you quote the Payment Reference above. It includes your full name (or as much as can fit in 18 characters) and Application ID, which is ESSENTIAL information.\n\nPlease also email {{$global->paymentEmail}} with your Application ID and amount you paid in the subject, to indicate that you have made a payment. In the email body, please include the names of the guests covered by the payment.\n    \nIf you believe this email to be in error, please send a reply to {{$global->ticketingEmail}}, quoting your Application ID.\n\nThank you for reserving {{$global->eventName}} tickets,\nWe look forward to seeing you on {{$global->eventFullDate}},\n\n{{$global->eventName}} Committee', '{{$global->eventName}} - Reminder of Payment Deadline Warning', 'To {{$primary->name}},\r\n\r\nThank you again for buying {{$global->eventName}} tickets.\r\nThis email is to let you know that we have not yet received full payment for your ticket(s), and the deadline for payment is Monday 20th February 2013.\r\n\r\nApplication ID: {{$application->appID}}\r\n\r\nTotal outstanding payment: {{$global->pound}}{{$application->totalToPay}}\r\n    \r\nPlease ensure the Treasurer receives your payment no later than 6pm on Monday 20th February 2012. After this time we will begin allocating tickets to the waiting list and therefore cannot guarantee your ticket(s).\r\nIf you have made payment in parts, there is no need to resend payments. The figure above may be incorrect as we will only mark your tickets valid once we have received the full payment. Please continue sending payment only up to the full amount and we will send confirmation once complete.\r\n\r\nPayment Information\r\n=================\r\nPlease make a bank transfer to ''{{$global->bankAccountName}}''.\r\nSort Code: {{$global->bankSortCode}}\r\nAccount Number: {{$global->bankAccountNumber}}\r\nPayment Reference: {{$application->paymentReference}}\r\n\r\nPlease make absolutely sure that you quote the Payment Reference above. It includes your full name (or as much as can fit in 18 characters) and Application ID, which is ESSENTIAL information.\r\n\r\nPlease also email {{$global->paymentEmail}} with your Application ID and amount you paid in the subject, to indicate that you have made a payment.\r\n    \r\nIf you believe this email to be in error, please send a reply to {{$global->ticketingEmail}}, quoting your Application ID.\r\n\r\nThank you for reserving {{$global->eventName}} tickets,\r\nWe look forward to seeing you on {{$global->eventFullDate}},\r\n\r\n{{$global->eventName}} Committee'),
(6, 'paymentSecondWarning', 1, 1, '{{$global->eventName}} - Payment Deadline Warning', 'To {{$primary->name}},\n\nThis email is to inform you that we have not yet received the full payment for your ticket application, and the deadline for payment has passed.\nPlease be advised that we will be re-allocating tickets to the waiting list very soon, and for that reason, we will require payment for your ticket before Midnight on THURSDAY THE 14TH FEBRUARY 2013.\n\nIf you have reserved tickets which you no longer need please notify the committee by sending an email to {{$global->paymentEmail}} \n\nPlease note that you may not receive a receipt of payment notification until Friday the 15th.\nAdditionally, please be aware that this email has only been sent to you, the primary ticket holder, and not your guests - whose payment you are responsible for.\n\n\nApplication ID: {{$application->appID}}\n\nTotal outstanding payment: {{$global->pound}}{{$application->totalToPay}}\n\nIf you have made payment in parts, there is no need to resend payments. The figure above may be incorrect as we will only mark your tickets valid once we have received the full payment. Please continue sending payment only up to the full amount and we will send confirmation once complete.\n\nPayment Information\n=================\nPlease make a bank transfer to: {{$global->bankAccountName}} (if you cannot put the full name in, include as much as possible)\nSort Code: {{$global->bankSortCode}}\nAccount Number: {{$global->bankAccountNumber}}\nPayment Reference: {{$application->paymentReference}}\n\nPlease make absolutely sure that you quote the Payment Reference above. It includes your full name (or as much as can fit in 18 characters) and Application ID, which is ESSENTIAL information.\n\nPlease also email {{$global->paymentEmail}} with your Application ID and amount you paid in the subject, to indicate that you have made a payment. In the email body, please include the names of the guests covered by the payment.\n    \nIf you believe this email to be in error, please send a reply to {{$global->ticketingEmail}}, quoting your Application ID.\nIf you have already made payment after Midnight on Tuesday the 12th, please disregard this email, as we should receive and process your payment soon.\n\nThank you for reserving {{$global->eventName}} tickets,\nWe look forward to seeing you on {{$global->eventFullDate}},\n\n{{$global->eventName}} Committee', '{{$global->eventName}} - Payment Deadline Warning', 'To {{$primary->name}},\r\n\r\nThis email is to inform you that we have not yet received the full payment for your ticket(s), and the deadline for payments was this evening.\r\nPlease be advised that we will be re-allocating tickets to the waiting list very soon, and for that reason, we will require payment for your ticket before 6PM on WEDNESDAY 22ND FEBRUARY 2012.\r\n\r\nApplication ID: {{$application->appID}}\r\n\r\nTotal outstanding payment: {{$global->pound}}{{$application->totalToPay}}\r\n\r\nIf you have made payment in parts, there is no need to resend payments. The figure above may be incorrect as we will only mark your tickets valid once we have received the full payment. Please continue sending payment only up to the full amount and we will send confirmation once complete.\r\n\r\nPayment Information\r\n=================\r\nPlease make a bank transfer to ''{{$global->bankAccountName}}''.\r\nSort Code: {{$global->bankSortCode}}\r\nAccount Number: {{$global->bankAccountNumber}}\r\nPayment Reference: {{$application->paymentReference}}\r\n\r\nPlease make absolutely sure that you quote the Payment Reference above. It includes your full name (or as much as can fit in 18 characters) and Application ID, which is ESSENTIAL information.\r\n\r\nPlease also email {{$global->paymentEmail}} with your Application ID and amount you paid in the subject, to indicate that you have made a payment.\r\n    \r\nIf you believe this email to be in error, please send a reply to {{$global->ticketingEmail}}, quoting your Application ID.\r\nIf you have already made payment at some point this afternoon, please disregard this, as we should find your payment soon.\r\n\r\nThank you for reserving {{$global->eventName}} tickets,\r\nWe look forward to seeing you on {{$global->eventFullDate}},\r\n\r\n{{$global->eventName}} Committee'),
(7, 'paymentFinalWarning', 1, 1, '{{$global->eventName}} - URGENT Final Payment Warning', 'To {{$primary->name}},\n\nThis email is to remind you that you have not yet paid for your Downing May Ball ticket(s), despite multiple deadline extensions.\nPayment must be made as soon as possible. If the payment has not been received by the treasurer at Midnight on Monday the 18th of February, we cannot guarantee your tickets as they will be re-allocated to the waiting list. This is the final reminder email.\n\nIf you believe this email should not have been sent to you, you must reply to ticketing@downingball.co.uk ASAP, quoting your Application ID.\nPlease also let us know if you believe you have made payment, or if you no longer require the tickets.\n\nAdditionally, please be aware that this email has only been sent to you, the primary ticket holder, and not your guests - whose payment you are responsible for.\n\n\nApplication ID: {{$application->appID}}\n\nTotal outstanding payment: {{$global->pound}}{{$application->totalToPay}}\n\n\nPayment Information\n=================\nPlease make a bank transfer to: {{$global->bankAccountName}} (if you cannot put the full name in, include as much as possible)\nSort Code: {{$global->bankSortCode}}\nAccount Number: {{$global->bankAccountNumber}}\nPayment Reference: {{$application->paymentReference}}\n\nPlease make absolutely sure that you quote the Payment Reference above. It includes your full name (or as much as can fit in 18 characters) and Application ID, which is ESSENTIAL information.\n\nPlease also email {{$global->paymentEmail}} with your Application ID and amount you paid in the subject, to indicate that you have made a payment. In the email body, please include the names of the guests covered by the payment.\n\nThank you for reserving {{$global->eventName}} tickets,\nWe look forward to seeing you on {{$global->eventFullDate}},\n\n{{$global->eventName}} Committee', '{{$global->eventName}} - URGENT Final Payment Warning', 'To {{$primary->name}},\r\n\r\nThis email is to remind you that you have not yet paid for your Jesus May Ball tickets, and that the deadline for payment was this evening at 6pm.\r\nPayment must be made as soon as possible, and so if you do not make payment within 24 HOURS of this email being sent, we cannot guarantee your tickets as they will be re-allocated to the waiting list.\r\n\r\nApplication ID: {{$application->appID}}\r\n\r\nTotal outstanding payment: {{$global->pound}}{{$application->totalToPay}}\r\n\r\nIf you believe this email should not have been sent to you, you must reply to mayball-tickets@jesus.cam.ac.uk ASAP, quoting your Application ID.\r\nPlease also let us know if you believe you have made payment.\r\n\r\nPayment Information\r\n=================\r\nPlease make a bank transfer to ''{{$global->bankAccountName}}''.\r\nSort Code: {{$global->bankSortCode}}\r\nAccount Number: {{$global->bankAccountNumber}}\r\nPayment Reference: {{$application->paymentReference}}\r\n\r\nPlease make absolutely sure that you quote the Payment Reference above. It includes your full name (or as much as can fit in 18 characters) and Application ID, which is ESSENTIAL information.\r\n\r\nPlease also email {{$global->paymentEmail}} with your Application ID and amount you paid in the subject, to indicate that you have made a payment.\r\n\r\nThank you for reserving {{$global->eventName}} tickets,\r\nWe look forward to seeing you on {{$global->eventFullDate}},\r\n\r\n{{$global->eventName}} Committee'),
(8, 'ticketCollection', 1, 1, 'Important: {{$global->eventName}} - Ticket Collection Information', 'Dear {{$primary->name}},\n\nTicket collection will be taking place on WEDNESDAY the 12th June from 10am to 1pm and THURSDAY the 13th June from 3pm to 6pm. \nPlease come to the Music Room, upstairs in E Staircase, Downing College. \n\nYou MUST bring photographic identification (University card, passport or UK driving licence). To make the collection process as easy as possible, it is desirable you know your Application ID â€“ though this is not essential for collection.\n\nProxies\n================\nOnly the main applicant is allowed to pick up the tickets and you must collect all the tickets on your application together. If you are not able to make it at the times specified, you may nominate a proxy to pick up the tickets. This proxy is required to bring their own ID and can be appointed by filling out the form available at: https://docs.google.com/forms/d/1AOjJ-R4FWYx7ZTwo2d4_7caqhA7vEcFQVufEQtEvUEo/viewform \n\nAlumni\n================\nIf you are an alumnus of Downing College and are unable to make the collection dates or organise a proxy, we can post tickets to you. To arrange this, please complete the form available in the link below by 2pm on TUESDAY the 11th June. Requests submitted after this point may not be dispatched in time to reach you before the Ball. The committee does not take responsibility for any tickets lost in transit, but we will try to help resolve the situation. https://docs.google.com/forms/d/1RGELIImOfEdFRlZH1aXhMgK0TF_q5yJBH3VcquFEMt8/viewform \n\nName Changes\n================\nPlease note that the cost of performing a name change is Â£20, but this will increase to Â£50 on the 15th of June. Changes need to have been requested and paid for by 10am on Monday the 17th June â€“ no name changes will be accepted after this point.\n\n\nApplication Details\n================\nPlease also take a moment to check that all of your details below are correct. You are reminded that photographic identification will be required on the evening to gain entry to the Ball. Those guests whose identification does not match the information on our database may be significantly delayed in entering the ball, or may be denied entry altogether.\n\nApplication ID: {{$application->appID}}\n\nYour Details:\n---------------------------\n{{$primary->name}}\n{{$primary->college}}\nPhone: {{$primary->phone}}\n\nOrder Summary\n---------------------------\n{{foreach $application->tickets as $guest}} {{$guest->name}} {{$guest->crsid}} - {{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} - Hash Code: {{$guest->hashCode}} \n{{endeach}}\n\n\nWe look forward to seeing you on {{$global->eventFullDate}}. Details for entry will be sent out in the near future.\n\nJack Scott\n{{$global->eventName}} Committee', '{{$global->eventName}} - Ticket Collection Information', 'Dear {{$primary->name}},\r\n\r\nTicket collection will be taking place on TUESDAY 12th June and WEDNESDAY 13th June, both from 12:30pm to 3pm. Please come to the Marshall Room in Jesus College during these times and BRING ID. Only the main applicant is allowed to pick up the tickets. You must collect all the tickets on your application together. If you are not able to make it at the times specified, you may nominate another person to pick up the tickets, but you must send me the name of this person in advance and they must also bring their ID. For those living outside of Cambridge, tickets may be collected from the Porters Lodge any day up to the day of the ball, or postage of tickets can also be arranged. In either of these cases you MUST let me know in advance.\r\n\r\nPlease also take a moment to check that all of your details below are correct.\r\n\r\nApplication Details:\r\n\r\nApplication ID: {{$application->appID}}\r\n\r\nYour Details\r\n============\r\n{{$primary->name}}\r\n{{$primary->college}}\r\n{{$primary->address}}\r\nPhone: {{$primary->phone}}\r\n\r\nOrder Summary\r\n================\r\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} {{$guest->hashCode}} {{$guest->crsid}}\r\n{{endeach}}\r\n\r\nTotal: {{$global->pound}}{{$application->totalCost}}\r\n\r\nThank you for buying {{$global->eventName}} tickets,\r\nWe look forward to seeing you on {{$global->eventFullDate}},\r\n\r\n{{$global->eventName}} Committee'),
(9, 'ticketCollectionReminder', 1, 1, '{{$global->eventName}} - Ticket Collection Reminder', 'Dear {{$primary->name}},\n\nThis email is just a reminder that if you haven''t collected your ticket(s) yet, please come to the Marshall Room in Jesus College any time until 3pm today. After that it will be become more difficult to get ahold of your ticket(s). Please BRING ID, and remember that only the main applicant is allowed to pick up the tickets. You must collect all the tickets on your application together. If you are not able to make it today, another person may pick up the tickets, however they must be a current Cambridge student and bring their own university ID. For those living outside of Cambridge, tickets may be collected from the Porters Lodge any day up to the day of the ball. In either of these cases you MUST let me know in advance.\n\nPlease also take a moment to check that all of your details below are correct.\n\nApplication Details:\n\nApplication ID: {{$application->appID}}\n\nYour Details\n============\n{{$primary->name}}\n{{$primary->college}}\n{{$primary->address}}\nPhone: {{$primary->phone}}\n\nOrder Summary\n================\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} {{$guest->hashCode}} {{$guest->crsid}}\n{{endeach}}\n\nTotal: {{$global->pound}}{{$application->totalCost}}\n\nThank you for buying {{$global->eventName}} tickets,\nWe look forward to seeing you on {{$global->eventFullDate}},\n\n{{$global->eventName}} Committee', '{{$global->eventName}} - Ticket Collection Reminder', 'Dear {{$primary->name}},\r\n\r\nThis email is just a reminder that if you haven''t collected your ticket(s) yet, please come to the Marshall Room in Jesus College any time until 3pm today. After that it will be become more difficult to get ahold of your ticket(s). Please BRING ID, and remember that only the main applicant is allowed to pick up the tickets. You must collect all the tickets on your application together. If you are not able to make it today, another person may pick up the tickets, however they must be a current Cambridge student and bring their own university ID. For those living outside of Cambridge, tickets may be collected from the Porters Lodge any day up to the day of the ball. In either of these cases you MUST let me know in advance.\r\n\r\nPlease also take a moment to check that all of your details below are correct.\r\n\r\nApplication Details:\r\n\r\nApplication ID: {{$application->appID}}\r\n\r\nYour Details\r\n============\r\n{{$primary->name}}\r\n{{$primary->college}}\r\n{{$primary->address}}\r\nPhone: {{$primary->phone}}\r\n\r\nOrder Summary\r\n================\r\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} {{$guest->hashCode}} {{$guest->crsid}}\r\n{{endeach}}\r\n\r\nTotal: {{$global->pound}}{{$application->totalCost}}\r\n\r\nThank you for buying {{$global->eventName}} tickets,\r\nWe look forward to seeing you on {{$global->eventFullDate}},\r\n\r\n{{$global->eventName}} Committee'),
(10, 'nameChangeConfirmed', 1, 1, '{{$global->eventName}} {{$global->eventYear}} - Confirm Name Change', 'Dear {{$primary->name}};\n    \nYour Application ID: {{$application->appID}}\n    \nA name change request has been submitted to change the name of one of the tickets on your application. The details of this change are below:\n    \nName change number: {{$nameChange->changeID}} (for reference purposes)\n\nChange the ticket belonging to: {{$nameChange->oldName}} {{$nameChange->ticketHash}}\n\nNew name on ticket: {{$nameChange->newName}} (CRSid: {{$nameChange->newCRSid}}, college: {{$nameChange->newCollege}}, phone: {{$nameChange->newPhone}})\n\nEmail address of person who submitted change: {{$nameChange->submissionEmail}}\n    \nIf this request came from you, or someone you know completing the change on your behalf, then please click the link below to confirm that you authorise the change:\n    \n{{$global->eventWebsiteTickets}}namechange.php?mode=authorise&id={{$nameChange->changeID}}&code={{$nameChange->secret}}\n\nThe name change will cost {{$global->pound}}{{$global->nameChangeCost}}. Please make a bank transfer using the following details:\n\nAccount name: {{$global->bankAccountName}}\nAccount number: {{$global->bankAccountNumber}}\nSort code: {{$global->bankSortCode}}\nPayment reference: {{$nameChange->changeID}}CHANGE\n\nPAYMENT MUST BE MADE BY 10 AM ON MONDAY THE 17TH OF JUNE. \nOtherwise the name change will not be processed.\n    \nIf there is something wrong with the name change and you need to correct it, or you don''t recognise the change (check that none of your guests submitted it), then click the link below to CANCEL the change:\n{{$global->eventWebsiteTickets}}namechange.php?mode=cancel&id={{$nameChange->changeID}}&code={{$nameChange->secret}}\n\nCollection for any tickets changed will be on Monday the 17th of June, between 5 and 6 pm, on the ground floor in Downing College''s Butterfield Cafe/Bar. Please bring photographic ID and know your application number.\n\nPlease note that this email was only sent to you, the primary ticket holder, not any of your guests (who may have initiated the name change and might want a copy).\n\nBest wishes,\nJack Scott\n\nHead of Sales, {{$global->eventName}}\n\n\n\nPlease note that this email was sent automatically after a name change was submitted from a public address. If you find that the form is being abused to send you junk emails, please follow the link below to have your address blocked from all future name change submissions:\n\n{{$global->eventWebsiteTickets}}namechange.php?mode=block&id={{$nameChange->changeID}}&code={{$nameChange->secret}}', '{{$global->eventName}} {{$global->eventYear}} - Confirm Name Change', 'Dear {{$primary->name}};\r\n    \r\nYour Application ID: {{$application->appID}}\r\n    \r\nA name change request has been submitted to change the name of one of the tickets on your application. The details of this change are below:\r\n    \r\nName change number: {{$nameChange->changeID}} (for reference purposes)\r\n\r\nChange the ticket belonging to: {{$nameChange->oldName}} {{$nameChange->ticketHash}}\r\n\r\nNew name on ticket: {{$nameChange->newName}} (CRSid: {{$nameChange->newCRSid}}, college: {{$nameChange->newCollege}}, phone: {{$nameChange->newPhone}})\r\n\r\nEmail address of person who submitted change: {{$nameChange->submissionEmail}}\r\n    \r\nIf this request came from you, or someone you know completing the change on your behalf, then please click the link below to confirm that you authorise the change:\r\n    \r\n{{$global->eventWebsiteTickets}}namechange.php?mode=authorise&id={{$nameChange->changeID}}&code={{$nameChange->secret}}\r\n\r\nThe name change will cost {{$global->pound}}10. Please make a bank transfer using the following details:\r\n\r\nAccount name: {{$global->bankAccountName}}\r\n\r\nAccount number: {{$global->bankAccountNumber}}\r\n\r\nSort code: {{$global->bankSortCode}}\r\n\r\nPayment reference: {{$nameChange->changeID}}CHANGE\r\n\r\n    \r\nIf there is something wrong with the name change and you need to correct it, or you don''t recognise the change (check that none of your guests submitted it), then click the link below to CANCEL the change:\r\n\r\n    \r\n{{$global->eventWebsiteTickets}}namechange.php?mode=cancel&id={{$nameChange->changeID}}&code={{$nameChange->secret}}\r\n\r\nKind regards from the {{$global->eventName}} Tickets Officer\r\n\r\n    \r\nPlease note that this email was sent automatically after a name change was submitted from a public address. If you find that the form is being abused to send you junk emails, please follow the link below to have your address blocked from all future name change submissions:\r\n\r\n{{$global->eventWebsiteTickets}}namechange.php?mode=block&id={{$nameChange->changeID}}&code={{$nameChange->secret}}\r\n\r\nIf you need to make a name change after following this link, then you will need to contact the tickets officer at {{$global->ticketingEmail}}'),
(11, 'confirmPayment', 1, 1, '{{$global->eventName}} Ticket Booking Confirmation', 'To {{$primary->name}},\nThank you for buying {{$global->eventName}} tickets. This email is to confirm that we have received full payment for your ticket(s).\nPlease see your details below to make sure that everything is correct, as this is what will be printed on the tickets.\n\nApplication ID: {{$application->appID}}\n\nYour Details\n============\n{{$primary->name}}\n{{$primary->college}}\n{{$primary->address}}\nPhone: {{$primary->phone}}\n\nOrder Summary\n================\n{{foreach $application->tickets as $guest}} {{$guest->name}} ({{$guest->crsid}}) - {{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} - {{$guest->hashCode}} \n{{endeach}}\n\nTotal: {{$global->pound}}{{$application->totalCost}}\n\nIf you need to change the name on any ticket, because you or one of your guests can no longer attend, then please fill in the details of the change at this website: http://www.downingball.co.uk/tickets/namechange.php\nIf you notice any mistakes with your application, then please reply to this email with details of the mistake.\n\nThank you for purchasing {{$global->eventName}} tickets,\nWe look forward to seeing you on {{$global->eventFullDate}},\n\n{{$global->eventName}} Committee', '{{$global->eventName}} Ticket Booking Confirmation', 'To {{$primary->name}},\r\nThank you for buying {{$global->eventName}} tickets. This email is to confirm that we have received full payment for your ticket(s).\r\nPlease see your details below to make sure that everything is correct, as this is what will be printed on the tickets.\r\n\r\nApplication ID: {{$application->appID}}\r\n\r\nYour Details\r\n============\r\n{{$primary->name}}\r\n{{$primary->college}}\r\n{{$primary->address}}\r\nPhone: {{$primary->phone}}\r\n\r\nOrder Summary\r\n================\r\n{{foreach $application->tickets as $guest}}{{$guest->ticketType}} - {{$global->pound}}{{$guest->price}} {{$guest->charity}} {{$guest->hashCode}} {{$guest->crsid}}\r\n{{endeach}}\r\n\r\nTotal: {{$global->pound}}{{$application->totalCost}}\r\n\r\nIf you need to change the name on any ticket, because you or one of your guests can no longer attend, then please fill in the details of the change at this website:\r\n{{$global->eventWebsite}}namechange.php \r\nIf you notice any minor mistakes with your application, then please reply to this email with details of the mistake.\r\n\r\nThank you for purchasing {{$global->eventName}} tickets,\r\nWe look forward to seeing you on {{$global->eventFullDate}},\r\n\r\n{{$global->eventName}} Committee'),
(12, 'nameChangeComplete', 1, 1, '{{$global->eventName}} {{$global->eventYear}} - Confirm Name Completed', 'Dear {{$primary->name}};\n    \nYour Application ID: {{$application->appID}}\n    \nThis email is to inform you that a name change attached to your application has been successfully paid for and completed. The details of this change are below:\n    \nName change number: {{$nameChange->changeID}} (for reference purposes)\n\nChange the ticket belonging to: {{$nameChange->oldName}} {{$nameChange->ticketHash}}\n\nNew name on ticket: {{$nameChange->newName}} (CRSid: {{$nameChange->newCRSid}}, college: {{$nameChange->newCollege}}, phone: {{$nameChange->newPhone}})\n\nPlease note that this email was only sent to you, the primary ticket holder, not any of your guests (who may have initiated the name change and might want a copy).\n\nCollection for this ticket will be on Monday the 17th of June, between 5 and 6 pm in Downing College''s Butterfield Cafe/Bar.\nEither you, a guest, or another friend can collect the ticket(s) but the collector must have photo ID (uni card is fine). Please ensure the collector knows the Application ID ( {{$application->appID}} ).\n\n\nBest wishes,\nJack Scott\nHead of Sales, {{$global->eventName}}', '{{$global->eventName}} {{$global->eventYear}} - Confirm Name Completed', 'Dear {{$primary->name}};\r\n    \r\nYour Application ID: {{$application->appID}}\r\n    \r\nThis email is to inform you that a name change attached to your application has been successfully paid for and completed. The details of this change are below:\r\n    \r\nName change number: {{$nameChange->changeID}} (for reference purposes)\r\n\r\nChange the ticket belonging to: {{$nameChange->oldName}} {{$nameChange->ticketHash}}\r\n\r\nNew name on ticket: {{$nameChange->newName}} (CRSid: {{$nameChange->newCRSid}}, college: {{$nameChange->newCollege}}, phone: {{$nameChange->newPhone}})\r\n\r\nKind regards from the {{$global->eventName}} Tickets Officer'),
(13, 'waitingList', 1, 1, '{{$global->eventName}} - Confirmation of Waiting List Addition', 'To {{$waitingList->name}},\n\nThank you for joining the waiting list for {{$global->eventName}} tickets. This email confirms that you have been added to the waiting list. You will be contacted if enough tickets become available to satisfy your order. \n\nYour Details\n============\n{{$waitingList->name}}\n{{$waitingList->college}}\n{{$waitingList->crsid}}\n{{$waitingList->email}}\n{{$waitingList->phone}}\n{{$waitingList->noOfTickets}} ticket(s)\n{{$waitingList->desiredTicketType}}\n============\n\nThank you for requesting {{$global->eventName}}.\nWe look forward to potentially seeing you on {{$global->eventFullDate}}\n\n{{$global->eventName}} Committee', '{{$global->eventName}} - Confirmation of Waiting List Addition', 'To {{$waitingList->name}},\r\n\r\nThank you for joining the waiting list for {{$global->eventName}} tickets. This email confirms that you have been added to the waiting list. You will be contacted if enough tickets become available to satisfy your order. \r\nYour Details\r\n============\r\n{{$waitingList->name}}\r\n{{$waitingList->college}}\r\n{{$waitingList->crsid}}\r\n{{$waitingList->email}}\r\n{{$waitingList->phone}}\r\n{{$waitingList->noOfTickets}} ticket(s)\r\n{{$waitingList->desiredTicketType}}\r\n============\r\n\r\nThank you for requesting {{$global->eventName}}.\r\nWe look forward to potentially seeing you on {{$global->eventFullDate}}\r\n\r\n{{$global->eventName}} Committee'),
(14, 'dietaryRequirements', 1, 0, '{{$global->eventName}} - Dietary Requirements for Dining Guests', 'To {{$primary->name}},\n\nThe Food Team are in the process of planning the menu for your exquisite culinary odyssey, which promises to be sensational.\n\nIn order to cater to all dietary requirements, please respond to this email by the 31st of March, detailing any dietary requirements or allergies that you or your guests have, so we can take this into consideration when creating your meal.\n\nEven if you have no specific requirements, please reply stating â€˜no requirementsâ€™ so we know that you have all received the message.\n\nPlease be aware that this email has only been sent to you, the primary ticket holder, and that you will need to communicate with your guests about their dietary requirements.\n\nPrepare your taste buds, and we will see you for your Eastern Odyssey in June.\n\nBest wishes,\nThe May Ball Committee', '', ''),
(15, 'Moss Bros Suit Hire', 1, 0, 'Downing May Ball 2013 - Discount: Moss Bros Suit Hire ', 'â€‹Dear Downing May Ball Guest,\n\nFirstly, apologies to the ladies - this is only relevant for our male ticket holders! Guys, read on...!\n\nDowning May Ball has teamed up with Moss Bros Suit Hire to offer all male ticket holders an exclusive, one-off discount for hiring a full dinner suit for the May Ball. Moss Bros is one of the UK''s top menswear stores, specialising in dress wear for formal occasions. They have 160 years of experience in formal dress hire, and would be sure to make you look the part for the big night.\n\nMoss Bros''s ''Hilton'' package - which includes a black, one-button, notch-collar dinner jacket, matching dress trousers, a shirt and a bowtie - normally retails at Â£75 for 4-day hire. As Downing May Ball guests, you will have the opportunity to have this deal at the reduced price of Â£49. Not only is this significantly cheaper, but you can still hire the dinner suit for 4 days so it may well be you can wear it more than once if you are attending other balls too.\n\nTo claim this offer, simply click on this link, print off the voucher and present it in store : http://downingball.co.uk/Moss-Bros-Discount-Voucher.pdf There are two Moss Bros stores in Cambridge - one is situated in the centre of town, on Petty Cury street, and the other on St. John''s Street. The discount offer is valid at either. When filling in the hire form after using the offer in store, please write ''Downing 2013'' as the ''Party name'', just to help the store keep track of orders.\nâ€‹Please note that this email has only been sent to you, the primary ticket holder, and you will need toâ€‹ forward it to your guests if they are to benefit from this special offer. \n\nFor more information, please have a browse of their website: http://www.moss.co.uk/\n\nI hope that this offer sounds appealing - please email if you have any questions but otherwise enjoy this privilege as Downing May Ball guests!\n\nBest wishes,\nJulia Rose\nSponsorship, Downing May Ball 2013â€‹\n\n---\nAny opinions  â€‹expressed in this email are those of the individual and not necessarily those of Downing College May Bâ€‹â€‹all Committee. This email and any attachments are confidential to Downing College May Ballâ€‹â€‹ Committee and are solely for the use of the intended recipient. Nothing in this email is to be taken as an agreement, representation, warranty or undertaking of any nature. No contract will be concluded with Downing College May Ball Committee until the terms have been agreed and a written Downing College May Ball 2013 contract has been physically signed by both parties. Please note that Downing College May Ball Committee is entirely separate from Downing College.', '', '');
INSERT INTO `emails` (`id`, `name`, `appOrTicket`, `builtIn`, `subject`, `body`, `defaultSubject`, `defaultBody`) VALUES
(16, 'Downing May Ball App', 1, 0, 'Downing May Ball App - Programme Updates and Live Push Notifications', 'Dear {{$primary->name}},\n\nAs you might have heard, this year we''re going to try something brand new in the world of Cambridge May Balls: live push notifications via an iPhone app.\nOn the night, we will deliver a personalised live feed of what''s happening around the ball straight to your phone''s lock screen. Just light it up and check out the latest info about the events that you choose.\nIn addition to that, our app keeps you up to speed with programme updates, which will reveal various parts of the ball as the day progresses.\n\nWe hope this app will enable a level of interactivity that just would not be possible on a paper medium.\nRest easy though - we still have detailed and easy-to-use regular programmes!\n\nSo head over to http://itun.es/i6xg4TH and download our app. If you have done so already, please update to the latest version on the App Store to ensure that the notifications work properly.\n\nSee you tonight!\n\nThe Downing May Ball Committee', '', '');

-- --------------------------------------------------------

--
-- Table structure for table `name_changes`
--

CREATE TABLE IF NOT EXISTS `name_changes` (
  `name_change_id` int(32) NOT NULL AUTO_INCREMENT,
  `secret` varchar(100) NOT NULL,
  `complete` tinyint(1) NOT NULL DEFAULT '0',
  `cancelled` tinyint(1) NOT NULL DEFAULT '0',
  `auto` tinyint(1) NOT NULL DEFAULT '0',
  `app_id` int(32) NOT NULL,
  `ticket_hash` varchar(10) DEFAULT NULL,
  `full_name` varchar(255) NOT NULL,
  `new_title` varchar(5) NOT NULL,
  `new_first_name` varchar(50) NOT NULL,
  `new_last_name` varchar(50) NOT NULL,
  `new_crsid` varchar(8) DEFAULT NULL,
  `new_college` varchar(100) DEFAULT NULL,
  `new_phone` varchar(13) DEFAULT NULL,
  `submitter_email` varchar(100) NOT NULL,
  `authorised` tinyint(1) NOT NULL DEFAULT '0',
  `paid` tinyint(1) NOT NULL DEFAULT '0',
  `created_at` datetime NOT NULL,
  `cost` int(11) NOT NULL DEFAULT '10',
  PRIMARY KEY (`name_change_id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Table structure for table `payments`
--

CREATE TABLE IF NOT EXISTS `payments` (
  `payment_id` int(32) NOT NULL AUTO_INCREMENT,
  `app_id` int(32) NOT NULL,
  `amount` decimal(10,2) NOT NULL,
  `method` varchar(100) NOT NULL,
  `valid` tinyint(4) NOT NULL,
  `notes` text,
  `created_at` datetime NOT NULL,
  PRIMARY KEY (`payment_id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Table structure for table `tickets`
--

CREATE TABLE IF NOT EXISTS `tickets` (
  `ticket_id` int(32) NOT NULL AUTO_INCREMENT,
  `app_id` int(32) NOT NULL,
  `title` varchar(20) NOT NULL,
  `first_name` varchar(50) NOT NULL,
  `last_name` varchar(50) NOT NULL,
  `crsid` varchar(8) DEFAULT NULL,
  `email` varchar(100) DEFAULT NULL,
  `college` varchar(100) NOT NULL,
  `phone` varchar(13) NOT NULL,
  `address` text NOT NULL,
  `hash_code` varchar(10) NOT NULL,
  `ticket_type_id` int(32) NOT NULL,
  `price` decimal(10,2) NOT NULL,
  `valid` tinyint(1) NOT NULL,
  `deleted` tinyint(1) NOT NULL DEFAULT '0',
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `collected_at` datetime NOT NULL,
  `entered_at` datetime NOT NULL,
  `entered_via` varchar(255) NOT NULL,
  `charity_donation` tinyint(1) NOT NULL,
  PRIMARY KEY (`ticket_id`),
  UNIQUE KEY `hash_code` (`hash_code`),
  UNIQUE KEY `crsid` (`crsid`),
  UNIQUE KEY `email` (`email`),
  KEY `app_id` (`app_id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Table structure for table `ticket_types`
--

CREATE TABLE IF NOT EXISTS `ticket_types` (
  `ticket_type_id` int(32) NOT NULL AUTO_INCREMENT,
  `ticket_type` varchar(50) NOT NULL,
  `price` decimal(10,2) NOT NULL,
  `available` tinyint(1) NOT NULL,
  `maximum` int(32) NOT NULL,
  `restricted` tinyint(1) NOT NULL,
  `use_other_max` int(32) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ticket_type_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=54 ;

--
-- Dumping data for table `ticket_types`
--

